import pdfmake from 'pdfmake/build/pdfmake.js';
import vfsFonts from 'pdfmake/build/vfs_fonts.js';
import PdfPrinter from 'pdfmake';
import express from "express";
import { response } from "../utils/response.mjs";
import fs from 'fs';
import { getEventsByEventId } from '../models/posterModule.mjs';
import path from 'path';


const posterController = express.Router(); 
const base64BackgroundImage = 'data:image/jpeg;base64,'; 

const  base64LogoImage = 'data:image/jpeg;base64,';

/**
 * 
 * 
 * 
 * 
 */
pdfmake.vfs = vfsFonts.pdfMake.vfs;

function createBannerContent(eve, base64LogoImage) {
    return {
        layout: {
            defaultBorder: false,
        },
        margin: [0, 20],
        table: {
            widths: ['auto', '*'],
            body: [
                [
                    {
                        image: base64LogoImage,
                        width: 100,
                        alignment: 'left',
                        margin: [20, 0, 0, 0], 
                    },
                    {
                        text: `${eve.event_name}`,
                        fontSize: 30,
                        bold: true,
                        alignment: 'center',
                        margin: [0, 0, 20, 0], 
                        color: 'white',
                    }
                ],
                [
                    {
                        text: `Event Venue: ${eve.event_venu}`,
                        colSpan: 2,
                        alignment: 'center',
                        margin: [0, 20], 
                        color: 'white',
                    },
                ],
                [
                    {
                        text: `Start Time: ${eve.event_start_time}`,
                        style: 'subheader',
                        alignment: 'center',
                        margin: [0, 10],
                        color: 'white',
                    },
                    {
                        text: `End Time: ${eve.event_end_time}`,
                        style: 'subheader',
                        alignment: 'center',
                        margin: [0, 10],
                        color: 'white',
                    }
                ],
                [
                    {
                        text: `Main Guest: ${eve.main_guest}`,
                        colSpan: 2,
                        alignment: 'center',
                        margin: [0, 10],
                        color: 'white',
                    }
                ],
            ]
        }
    };  
}

function createDocDefinition(event, base64BackgroundImage, base64LogoImage) {
    const bannerContent = createBannerContent(event, base64LogoImage);

    return {
        pageSize: 'A4',
        background: function(currentPage) {
            return {
                image: base64BackgroundImage,
                width: 595.28, 
                height: 841.89 
            };
        },
        pageMargins: [40, 60, 40, 60],
        content: [bannerContent],
        styles: {
            subheader: {
                fontSize: 18,
                bold: true,
                alignment: 'center',
                margin: [0, 10],
                color: 'white'
            }
        }
    };
}

async function generatePDF(event, base64BackgroundImage, base64LogoImage) {
    try {
        const printer = new PdfPrinter({
            Roboto: {
                normal: fs.readFileSync('/home/dell/sy/back/BACKEND/asset/fonts/Roboto-Regular.ttf'),
                bold: fs.readFileSync('/home/dell/sy/back/BACKEND/asset/fonts/Roboto-Bold.ttf'),
                italics: fs.readFileSync('/home/dell/sy/back/BACKEND/asset/fonts/Roboto-Italic.ttf'),
                bolditalics: fs.readFileSync('/home/dell/sy/back/BACKEND/asset/fonts/Roboto-BoldItalic.ttf')
            }
        });

        console.log("printer");
        const docDefinition = createDocDefinition(event, base64BackgroundImage, base64LogoImage);
        const pdfDoc = printer.createPdfKitDocument(docDefinition);
        console.log("pdfdoc");

        const stream = fs.createWriteStream(`/home/dell/sy/back/BACKEND/asset/uplod/${event.event_name}.pdf`);
        pdfDoc.pipe(stream);
        pdfDoc.end();

        stream.on('finish', () => {
            console.log('PDF generated successfully!');
        });

        stream.on('error', (err) => {
            console.error('Error generating PDF:', err);
        });
    } catch (error) {
        console.error('Error generating PDF:', error);
    }
}



posterController.get("/:event_id", async (req, res) => {
    try {
        const eventId = req.params.event_id;
        const events = await getEventsByEventId(eventId);
        var event = events[0];
        await generatePDF(event, base64BackgroundImage, base64LogoImage);
        return response.success(res,`/home/dell/sy/back/BACKEND/asset/uplod/${event.event_name}.pdf`);
    } catch (error) {
        return response.error(res, error.message);
    }   
});
        


export default posterController;
